import React from "react";

function MoviesCard({ name, duration, image, isSaved }) {

  return (
    <li className="moviescard">
      <button type="button" className={`${isSaved ? "moviescard__button_active" : "moviescard__button"}`}></button>
      <h2 className="moviescard__name">{name}</h2>
      <p className="moviescard__duration">{duration}</p>
      <image className="moviescard__image" src={image} alt={name} />
    </li>
  )
}

export default MoviesCard;
